import { NavLink } from 'react-router-dom';

const Header = () => {
  return (
    <nav>
      {/* A diferença entre o 'link' e o 'navLink' é que quando eu clicar em um navLink de uma página e for para ela, ele adiciona no meu HTML uma classe 'active' a página ativa, podendo asim fazer manipulações com css por exemplo */}
      <NavLink to="/" end  activeStyle={{color: to}}>Home</NavLink> | 
      <NavLink to="sobre">Sobre</NavLink>
      {/* Porem s eobservar o '/' estará presente em todos os diretório, então quando é a página home, temos que especificar colcoando um 'end' */}
    </nav>
  );
};

export default Header;
