import React from 'react'
import { useParams } from 'react-router-dom'

const Produto = () => {
  const params = useParams()
  console.log(params.id) // me retornando o id, que é 'notebook' no caso do exemplo
  console.log(params) // me retornando um obj com o nome que eu defini no 'app.js' na ora dos paramentro que foi 'produto/:id', neste caso se eu clicar em 'notebook' ele me retorna: {id: 'notebook'}

  // usando isso para eu fazer um fetch, e no final desse fetch eu puxo o paramentro do produto que eu quero puxar


  return (
    <div>
      <h1>Produto: {params.id}</h1> {/* Irá aparecer o nome do itme clidado */}
    </div>
  )
}

export default Produto
